import { Router } from "express";
import userController from "../controllers/user";
import tugasController from "../controllers/tugasController";

const router = new Router();

router.get("/user", userController.getUsers);
router.get("/user/:id", userController.getUser);
router.post("/user", userController.createUser);
router.put("/user/:id", userController.updateUser);
router.delete("/user/:id", userController.deleteUser);

router.get("/customers", tugasController.getCustommerInformation);
// router.get("/product/:name", tugasController.getProdukBycategory);
router.get("/product", tugasController.getProdukBycategory);
router.get("/orders", tugasController.getCustomerDetailOrder);

export default router;

